package loops;

public class ArraySum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum =0;
		int x[]= {100,200,300,400,500,600,700,800,900,1000};
	      for (int i=0;i<=x.length-1;i++)
	      {
	    	  sum = sum+x[i];
	{
		System.out.println(sum);
	}
	      }
	}

}
